import { handleActions } from '@letapp/redux-actions';
import * as actions from './todosActions';

const initialState = {
    todos:[],
    isLoading: false,
    isError: false,
};

export default handleActions({
    [actions.addTodo.start]: (state)=>({
        ...state,
        isError: false,
        isLoading: true,
    }),
    [actions.addTodo.success]: (state, actions)=>({
        ...state,
        isLoading: false,
        todos: state.todos.concat(actions.payload),
    }),
    [actions.addTodo.error]: (state)=>({
        ...state,
        isLoading: false,
        isError: true,
    }),
    [actions.Todo.start]: (state)=>({
        ...state,
        isError: false,
        isLoading: true,
    }),
    [actions.Todo.success]: (state, actions)=>({
        ...state,
        isLoading: false,
        todos: state.todos.concat(actions.payload),
    }),
    [actions.Todo.error]: (state)=>({
        ...state,
        isLoading: false,
        isError: true,
    }),
    [actions.clickOnTodo]: (state, actions)=>{      
        const newTodos = [...state.todos];        
        newTodos.find((it)=>it.id===actions.payload).completed = !newTodos.find((it)=>it.id===actions.payload).completed;
        return ({
            ...state,
            todos: newTodos, 
        })
    },
   
}, initialState);
