import { handleActions } from '@letapp/redux-actions';
import * as actions from './actions';

const initialState = {
    todo:[],
};

export default handleActions({
    [actions.addTodo]: (state, actions)=>({
        todos: state.todo.concat(actions.paylo)
    })
}, initialState);
