import React from "react";

function Item({text, id, completed, handleItemOnClick, handleDeleteItem}){
  return(
    <li onClick={handleItemOnClick(id)} style={{display:"flex", justifyContent}}>
      <p className={completed?'completed':''} >
      {text}
      </p>
      <button onClick={handleDeleteItem(id)}>delete</button>
    </li>
  )
}

export default Item ;
