import * as actions from './todosActions';


export function addTodo(todo) {
    return async function addTodoThunk(dispatch, getState) {
        try{
            dispatch(actions.addTodo.start());
            await setTimeout(()=>{dispatch(actions.addTodo.success(todo))},200);            
        } catch (err) {
            dispatch(actions.addTodo.error())
        }
    }
}

export function clickOnTodo(id) {
    return function clickOnTodoThunk(dispatch) {
        dispatch(actions.clickOnTodo.start());            
    }
}

export { actions };
