import { createAsyncActions, createAction } from '@letapp/redux-actions';

export const addTodo = createAsyncActions('todos/ADD_TODO');
export const deleteTodo = createAsyncActions('todos/D_TODO');
export const clickOnTodo = createAction('todos/CLICK_ON_TODO');
