import React from "react";
import ListItem from './components/ListItem';
import T from 'prop-types';
import uuid from 'uuid/v4'
import { todosOperations, todosSelectors } from './modules/todos';
import { Link, Route } from 'react-router-dom';
import { compose, withState, withHandlers } from 'recompose';
import { connect } from 'react-redux';

function App({
  setInputState,
  list,
  inputState,
  handleOnChange,
  handleOnClick,
  handleItemOnClick,
}) {
  return (
    <div className="App">
      <input onChange={(evt)=>setInputState(evt.target.value)} value={inputState} />
      <button onClick={handleOnClick}>Додати</button>
      <div>
        <Link to='/'>Всі</Link>
        <Link to='/new'>Нові</Link>
        <Link to='/completed'>Виконані</Link>
      </div>
      <Route exact  path='/' render={()=>
        <ListItem 
          items={list} 
          handleItemOnClick={handleItemOnClick}
        />
      } />
      <Route path='/new' render={()=>
        <ListItem 
          items={list.filter((item)=>item.completed===false)} 
          handleItemOnClick={handleItemOnClick}
        />
      } />
      <Route path='/completed' render={()=>
        <ListItem 
        items={list.filter((item)=>item.completed===true)} 
          handleItemOnClick={handleItemOnClick}
        />
      }/>      
    </div>
  );
}

App.propTypes = {
  appState: T.shape({
    inputValue: T.string,
    items: T.arrayOf(T.shape({
      text: T.string,
      id: T.number,
      completed:T.bool
    }))
  }),
  handleOnChange: T.func,
  handleOnClick: T.func,
  handleItemOnClick: T.func,
}

const mapDispatchToProps = {
  addTodo: todosOperations.addTodo,
}
const mapStateToProps = (state)=> ({

  list: todosSelectors.getTodos(state),
})

export default compose(
  connect(mapStateToProps, mapDispatchToProps),
  withState('inputState', 'setInputState', ''),
  withHandlers({
    handleOnClick: (props)=>()=>{
      const newTodo = {
        text:props.inputState,
        id: uuid(),
        completed: false,
      };
      props.addTodo(newTodo);
      props.setInputState('');
    },
    handleItemOnClick: (props)=> (id)=> ()=>{
      props.list((appState)=>{
        let newState= {...appState};
        newState.items[id].completed = !newState.items[id].completed;
        return newState
      }) 
    }
  })
)(App);
