import { handleActions } from '@letapp/redux-actions';
import * as actions from './todosActions';

const initialState = {
    todos:[],
    isLoading: false,
    
};

export default handleActions({
    [actions.addTodo.start]: (state, actions)=>({
        ...state,
        isError: false,
        isLoading: true,
    }),
    [actions.addTodo.success]: (state, actions)=>({
        ...state,
        isLoading: false,
        todos: state.todos.concat(actions.payload),
    }),
    [actions.addTodo.error]: (state, actions)=>({
        ...state,
        isLoading: false,
        isError: true,
    })
}, initialState);
