import { handleActions } from '@letapp/redux-actions';
import * as actions from './todosActions';

const initialState = {
    todos:[],
    isLoading: false,
    isError: false,
};

export default handleActions({
    [actions.addTodo.start]: (state)=>({
        ...state,
        isError: false,
        isLoading: true,
    }),
    [actions.addTodo.success]: (state, actions)=>({
        ...state,
        isLoading: false,
        todos: state.todos.concat(actions.payload),
    }),
    [actions.addTodo.error]: (state)=>({
        ...state,
        isLoading: false,
        isError: true,
    }),
    [actions.clickOnTodo]: (state, actions)=>{   
        //console.log('state.todos',state.todos)     
        const newTodos = state.todos.find((it)=>it.id===actions.payload);
        //console.log('newTodos',actions)
        newTodos.completed = !newTodos.completed;
        
        return ({
            ...state,
            todos: state.todos.concat(newTodos), 
        })
    },
   
}, initialState);
