import React from "react";
import { connect } from 'react-redux';
import { compose, withState, withHandlers } from 'recompose';
import Item from "./Item";

function ListItem({ items, handleItemOnClick }) {
  return (
    <ul>
      {items.map(item => (
        <Item
          key={item.id}
          text={item.text}
          id={item.id}
          completed={item.completed}
          handleItemOnClick={handleItemOnClick}
        />
      ))}
    </ul>
  );
}

const mapDispatchToProps = {
  addTodo: todosOperations.addTodo,
}
const mapStateToProps = (state)=> ({
  list: todosSelectors.getTodos(state),
  isLoading: todosSelectors.getLoadingStatus(state),
})

const enhancer = compose(
  connect(mapStateToProps)
)

export default enhancer(ListItem);
